import React, { useState, useEffect } from 'react';
import { getAllOsoby } from './services/api';

const App = () => {
    const [osoby, setOsoby] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const osobyData = await getAllOsoby();
                setOsoby(osobyData);
                setIsLoading(false);
            } catch (error) {
                console.error('Błąd podczas pobierania danych osób:', error);
                setIsLoading(false);
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <h1>Lista Osób</h1>
            {isLoading ? (
                <p>Ładowanie danych...</p>
            ) : (
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Imię</th>
                        <th>Nazwisko</th>
                    </tr>
                    </thead>
                    <tbody>
                    {osoby.map((osoba) => (
                        <tr key={osoba.id}>
                            <td>{osoba.id}</td>
                            <td>{osoba.imie}</td>
                            <td>{osoba.nazwisko}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default App;
