import React, { useState } from 'react';
import axios from 'axios';

const DodajOsobeForm = ({ onOsobaDodana }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [newOsoba, setNewOsoba] = useState({
        imie: '',
        nazwisko: '',
        dataUrodzenia: '',
        numerTelefonu: '',
        ulica: '',
        miasto: '',
        nazwaStanowiska: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewOsoba(prevOsoba => ({
            ...prevOsoba,
            [name]: value,
        }));
    };

    const handleDodajOsobe = () => {
        axios.post('http://localhost:8080/osoba', newOsoba)
            .then(response => {
                onOsobaDodana(response.data);
                setNewOsoba({
                    imie: '',
                    nazwisko: '',
                    dataUrodzenia: '',
                    numerTelefonu: '',
                    ulica: '',
                    miasto: '',
                    nazwaStanowiska: '',
                });
                setIsOpen(false); // Zamknij formularz po dodaniu osoby
            })
            .catch(error => console.error('Błąd dodawania osoby: ', error));
    };

    return (
        <div>
            <h3>Dodaj Nową Osobę</h3>
            {isOpen ? (
                <form>
                    <label>
                        Imię:
                        <input type="text" name="imie" value={newOsoba.imie} onChange={handleInputChange} />
                    </label>
                    <br />
                    <label>
                        Nazwisko:
                        <input type="text" name="nazwisko" value={newOsoba.nazwisko} onChange={handleInputChange} />
                    </label>
                    {/* Dodaj resztę pól formularza */}
                    <br />
                    <button type="button" onClick={handleDodajOsobe}>Dodaj</button>
                </form>
            ) : (
                <button onClick={() => setIsOpen(true)}>Otwórz formularz</button>
            )}
        </div>
    );
};

export default DodajOsobeForm;
