// Login.js
import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import { Link, Navigate } from 'react-router-dom';

const Login = () => {
    const { isLoggedIn, login } = useAuth();
    const [formData, setFormData] = useState({ username: '', password: '' });

    // Jeśli użytkownik jest już zalogowany, przekieruj go na stronę domową
    if (isLoggedIn) {
        return <Navigate to="/home" />;
    }

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleLogin = () => {
        // Tutaj możesz dodać logikę autoryzacji, np. wywołując API logowania
        // Przykładowa logika: Sprawdź, czy login i hasło są poprawne
        if (formData.username === 'exampleUser' && formData.password === 'examplePassword') {
            login();
        } else {
            alert('Nieprawidłowy login lub hasło');
        }
    };

    return (
        <div>
            <h2>Ekran Logowania</h2>
            <label>
                Login:
                <input type="text" name="username" value={formData.username} onChange={handleInputChange} />
            </label>
            <br />
            <label>
                Hasło:
                <input type="password" name="password" value={formData.password} onChange={handleInputChange} />
            </label>
            <br />
            <button onClick={handleLogin}>Zaloguj</button>
            <p>
                Nie masz jeszcze konta? <Link to="/register">Zarejestruj się</Link>
            </p>
        </div>
    );
};

export default Login;
