// OsobaList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DodajOsobeForm from './DodajOsobeForm';
import '../styles/OsobaList.css';

const OsobaList = () => {
    const [osoby, setOsoby] = useState([]);
    const [error, setError] = useState(false);
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:8080/osoba')
            .then(response => setOsoby(response.data))
            .catch(() => setError(true));
    }, []);

    const handleOsobaDodana = (nowaOsoba) => {
        setOsoby(prevOsoby => [...prevOsoby, nowaOsoba]);
    };

    const handleDodajOsobeClick = () => {
        setShowForm(true);
    };

    return (
        <div>
            <table className="osoba-table">
                {/* ... */}
            </table>
            <div>
                <h2>Lista Osób</h2>
                <button onClick={handleDodajOsobeClick}>Dodaj Osobę</button>
                {showForm && <DodajOsobeForm onOsobaDodana={handleOsobaDodana} />}
            </div>
        </div>
    );
};

export default OsobaList;
