// DodajOsobeForm.js
import React, { useState } from 'react';

const DodajOsobeForm = ({ onOsobaDodana }) => {
    const [imie, setImie] = useState('');
    const [nazwisko, setNazwisko] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Tutaj dodaj logikę wysyłania danych do serwera
        // Po udanym dodaniu osoby, wywołaj funkcję onOsobaDodana z nową osobą
        const nowaOsoba = {
            imie: imie,
            nazwisko: nazwisko,
            // Dodaj inne pola według potrzeb
        };

        // Przykład z użyciem funkcji onOsobaDodana
        onOsobaDodana(nowaOsoba);

        // Tutaj możesz dodać logikę wysyłania danych na serwer
        // np. poprzez wywołanie odpowiedniego endpointa z użyciem Axios lub Fetch
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Dodaj Osobę</h2>
            <label>
                Imię:
                <input type="text" value={imie} onChange={(e) => setImie(e.target.value)} />
            </label>
            <label>
                Nazwisko:
                <input type="text" value={nazwisko} onChange={(e) => setNazwisko(e.target.value)} />
            </label>
            {/* Dodaj inne pola według potrzeb */}
            <button type="submit">Dodaj Osobę</button>
        </form>
    );
};

export default DodajOsobeForm;
