import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/OsobaList.css';

const OsobaList = () => {
    const [osoby, setOsoby] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8080/osoba')
            .then(response => setOsoby(response.data))
            .catch(error => console.error('Błąd pobierania danych: ', error));
    }, []);

    const handleDodajOsobe = () => {
        // Tutaj możesz umieścić logikę otwierania formularza dodawania osoby
        console.log('Dodaj nową osobę');
    };

    return (
        <div>
            <h2>Lista Osób</h2>
            <table className="osoba-table">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Imię</th>
                    <th>Nazwisko</th>
                    <th>Data urodzenia</th>
                    <th>Numer telefonu</th>
                    <th>Adres</th>
                    <th>Praca</th>
                </tr>
                </thead>
                <tbody>
                {osoby.map(osoba => (
                    <tr key={osoba.id} className="osoba-row">
                        <td>{osoba.id}</td>
                        <td>{osoba.imie}</td>
                        <td>{osoba.nazwisko}</td>
                        <td>{osoba.dataUrodzenia}</td>
                        <td>{osoba.numerTelefonu ? osoba.numerTelefonu.numer : 'Brak'}</td>
                        <td>{osoba.adres ? `${osoba.adres.ulica}, ${osoba.adres.miasto}` : 'Brak'}</td>
                        <td>{osoba.praca ? osoba.praca.nazwaStanowiska : 'Brak'}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            <button onClick={handleDodajOsobe}>Dodaj Osobę</button>
        </div>
    );
};

export default OsobaList;
