// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import OsobaList from './components/OsobaList';

function App() {
    const [buttonClicked, setButtonClicked] = useState(false);
    const navigate = useNavigate(); // Używamy useNavigate do dynamicznego przekierowania

    const handleClick = () => {
        setButtonClicked(true);
        navigate('/osoba-lista'); // Po kliknięciu przycisku zmieniamy trasę
    };

    return (
        <Router>
            <div>
                {!buttonClicked ? (
                    <div>
                        <h1>Moja Aplikacja React</h1>
                        <button onClick={handleClick}>Kliknij mnie</button>
                    </div>
                ) : null}

                <Routes>
                    <Route path="/osoba-lista" element={<OsobaList />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
