// src/components/OsobaList.js
import React, { useState, useEffect } from 'react';
import { getAllOsoby } from '../services/api';

const OsobaList = () => {
    const [osoby, setOsoby] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchOsoby();
    }, []);

    const fetchOsoby = async () => {
        try {
            const response = await getAllOsoby();
            setOsoby(response.data); // Zakładam, że API zwraca dane w formacie { data: [...] }
            setIsLoading(false);
        } catch (error) {
            console.error('Błąd podczas pobierania danych:', error);
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h2>Lista Osób</h2>

            {isLoading ? (
                <p>Ładowanie danych...</p>
            ) : (
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Imię</th>
                        <th>Nazwisko</th>
                    </tr>
                    </thead>
                    <tbody>
                    {osoby.map((osoba) => (
                        <tr key={osoba.id}>
                            <td>{osoba.id}</td>
                            <td>{osoba.imie}</td>
                            <td>{osoba.nazwisko}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default OsobaList;
