import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { GuardProvider, GuardedRoute } from 'react-router-guards';
import LoginPage from './components/LoginPage';
import HomePage from './components/HomePage';
import OsobaPOST from './components/crud/OsobaPOST';
import OsobaDELETE from './components/crud/OsobaDELETE';
import OsobaPUT from './components/crud/OsobaPUT';
import AdresDELETE from './components/crud/AdresDELETE';

// Tworzymy funkcję, która sprawdzi, czy użytkownik jest zalogowany
const requireLogin = (to, from, next) => {
    // Sprawdzamy, czy użytkownik jest zalogowany
    if (isUserLoggedIn()) {
        // Jeśli tak, to pozwalamy na dostęp
        next();
    } else {
        // Jeśli nie, przenosimy go na stronę logowania
        next.redirect('/login');
    }
};

// Funkcja pomocnicza, aby sprawdzić, czy użytkownik jest zalogowany
const isUserLoggedIn = () => {
    // Tutaj możesz użyć dowolnej logiki sprawdzającej, czy użytkownik jest zalogowany
    // Na przykład, jeśli używasz kontekstu autentykacji, sprawdź, czy użytkownik jest zalogowany
    // Tutaj używam prostej zmiennej stanu w celach demonstracyjnych
    const [loggedIn, setLoggedIn] = useState(false);

    return loggedIn;
};

function App() {
    return (
        <Router>
            <GuardProvider guards={[requireLogin]} loading={<div>Loading...</div>}>
                <Routes>
                    <Route path="/login" element={<LoginPage />} />
                    <Route path="/home" element={<HomePage />} />
                    {/* Używamy GuardedRoute zamiast Route */}
                    <GuardedRoute path="/dodaj-osobe" element={<OsobaPOST />} />
                    <GuardedRoute path="/usun-osobe" element={<OsobaDELETE />} />
                    <GuardedRoute path="/edytuj-osobe" element={<OsobaPUT />} />
                    <GuardedRoute path="/usun-adres" element={<AdresDELETE />} />
                    {/* Dodaj poniższą trasę, aby przenosić użytkownika do /home po zalogowaniu */}
                    <Route path="/*" element={<Navigate to="/home" />} />
                </Routes>
            </GuardProvider>
        </Router>
    );
}

export default App;
