import React, { useEffect, useState } from 'react';
import { getAllOsoby } from '../services/api';
import NumerTelefonu from './NumerTelefonu'; // Sprawdź poprawność ścieżki do komponentu NumerTelefonu
import Adres from './Adres'; // Sprawdź poprawność ścieżki do komponentu Adres
import Praca from './Praca'; // Sprawdź poprawność ścieżki do komponentu Praca

const OsobaList = () => {
    const [osoby, setOsoby] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    const fetchOsoby = async () => {
        try {
            const response = await getAllOsoby();
            console.log(response.data); // Sprawdź, czy dane są prawidłowo pobierane
            setOsoby(response.data);
            setIsLoading(false);
        } catch (error) {
            console.error('Błąd podczas pobierania danych:', error);
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchOsoby();
    }, []); // Wywołaj fetchOsoby() tylko raz po zamontowaniu komponentu

    return (
        <div>
            {isLoading ? (
                <p>Ładowanie...</p>
            ) : (
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Imię</th>
                        <th>Nazwisko</th>
                        <th>Numer Telefonu</th>
                        <th>Adres</th>
                        <th>Praca</th>
                    </tr>
                    </thead>
                    <tbody>
                    {osoby && osoby.map((osoba) => (
                        <tr key={osoba.id}>
                            <td>{osoba.id}</td>
                            <td>{osoba.imie}</td>
                            <td>{osoba.nazwisko}</td>
                            <td><NumerTelefonu numer={osoba.numerTelefonu ? osoba.numerTelefonu.numer : 'Brak numeru'} /></td>
                            <td><Adres ulica={osoba.adres ? osoba.adres.ulica : 'Brak ulicy'} miasto={osoba.adres ? osoba.adres.miasto : 'Brak miasta'} /></td>
                            <td><Praca nazwaStanowiska={osoba.praca ? osoba.praca.nazwaStanowiska : 'Brak informacji o pracy'} /></td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default OsobaList;
