import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import './App.css';
import OsobaList from './components/OsobaList';

const KliknijPage = () => {
    return (
        <div>
            <h2>Strona z przyciskiem "Kliknij"</h2>
            <Link to="/osoby">Przejd≈∫ do tabeli z osobami</Link>
        </div>
    );
};

function App() {
    const [zalogowany, setZalogowany] = useState(false);

    const handleZalogujClick = () => {
        setZalogowany(true);
    };

    return (
        <Router>
            <div className="App">
                <h1>Aplikacja</h1>
                {!zalogowany ? (
                    <Link to="/kliknij" onClick={handleZalogujClick}>
                        Zaloguj
                    </Link>
                ) : null}
                <Routes>
                    <Route path="/osoby" element={<OsobaList />} />
                    <Route path="/kliknij" element={<KliknijPage />} />
                    <Route path="/" element={<Navigate to="/osoby" />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
