import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useHistory } from 'react-router-dom';
import Page from './Page';

function App() {
    const [isButtonClicked, setIsButtonClicked] = useState(false);
    const history = useHistory();

    const handleButtonClick = () => {
        setIsButtonClicked(true);
        // Po kliknięciu przycisku, używamy `history.push` do programowego przekierowania do trasy "/strona"
        history.push('/strona');
    };

    return (
        <Router>
            <div>
                <Routes>
                    <Route
                        path="/"
                        element={
                            isButtonClicked ? (
                                <Link to="/strona">Przejdź do strony</Link>
                            ) : (
                                <button onClick={handleButtonClick}>Kliknij mnie</button>
                            )
                        }
                    />
                    <Route path="/strona" element={<Page />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
