// src/components/OsobaList.js
import React, { useState, useEffect } from 'react';
import { getAllOsoby, getNumerTelefonu } from '../services/api';

const OsobaList = () => {
    const [osoby, setOsoby] = useState([]);
    const [numerTelefonu, setNumerTelefonu] = useState('');
    const [imie, setImie] = useState('');
    const [nazwisko, setNazwisko] = useState('');

    useEffect(() => {
        fetchOsoby();
    }, []);

    const fetchOsoby = async () => {
        try {
            const osobyData = await getAllOsoby();
            setOsoby(osobyData);
        } catch (error) {
            console.error('Błąd podczas pobierania danych:', error);
        }
    };

    const handleNumerTelefonuSubmit = async (e) => {
        e.preventDefault();

        try {
            const numerTelefonuData = await getNumerTelefonu(imie, nazwisko);
            setNumerTelefonu(numerTelefonuData);
        } catch (error) {
            console.error('Błąd podczas pobierania numeru telefonu:', error);
        }
    };

    return (
        <div>
            <h2>Lista Osób</h2>
            <ul>
                {osoby.map((osoba) => (
                    <li key={osoba.id}>{osoba.imie} {osoba.nazwisko}</li>
                ))}
            </ul>

            <h2>Szukaj Numeru Telefonu</h2>
            <form onSubmit={handleNumerTelefonuSubmit}>
                <label>
                    Imię:
                    <input type="text" value={imie} onChange={(e) => setImie(e.target.value)} />
                </label>
                <label>
                    Nazwisko:
                    <input type="text" value={nazwisko} onChange={(e) => setNazwisko(e.target.value)} />
                </label>
                <button type="submit">Szukaj Numeru Telefonu</button>
            </form>

            {numerTelefonu && (
                <div>
                    <h3>Numer Telefonu:</h3>
                    <p>{numerTelefonu}</p>
                </div>
            )}
        </div>
    );
};

export default OsobaList;
