import React, { useEffect, useState } from 'react';
import { getAllOsoby } from '../services/api';

const OsobaList = () => {
    const [osoby, setOsoby] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    const fetchOsoby = async () => {
        try {
            const response = await getAllOsoby();
            console.log(response.data); // Sprawdź, czy dane są prawidłowo pobierane
            setOsoby(response.data);
            setIsLoading(false);
        } catch (error) {
            console.error('Błąd podczas pobierania danych:', error);
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchOsoby();
    }, []); // Wywołaj fetchOsoby() tylko raz po zamontowaniu komponentu

    return (
        <div>
            {isLoading ? (
                <p>Ładowanie...</p>
            ) : (
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Imię</th>
                        <th>Nazwisko</th>
                    </tr>
                    </thead>
                    <tbody>
                    {osoby && osoby.map((osoba) => (
                        <tr key={osoba.id}>
                            <td>{osoba.id}</td>
                            <td>{osoba.imie}</td>
                            <td>{osoba.nazwisko}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default OsobaList;
